import axios from 'axios';
import { AUTH_START, AUTH_FAILED, AUTH_SUCCESS } from './actionTypes';

export const authStart = () => {
    return {
        type: AUTH_START
    }
};

export const authSuccess = (token, userId) => {
    return {
        type: AUTH_SUCCESS,
        token: token,
        userId: userId
    }
};

export const authFailed = (error) => {
    return {
        type: AUTH_FAILED,
        error: error
    };
};

export const logout = () => {}
export const checkAuthTimeOut = (expirationTime) => {
    return dispatch => {
        setTimeout(() => {

        }, expirationTime);
    };
};

export const auth = (email, password, isSignup = false) => {
    return dispatch => {
        dispatch(authStart());
        const authData = {
            email: email,
            password: password,
            returnSecureToken: true
        };

        let url = isSignup ?
            'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCXyYykiJQjUafDFB18wiFqDXo21AdZ6L8' :
            'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCXyYykiJQjUafDFB18wiFqDXo21AdZ6L8';

        axios.post(url, authData)
            .then(resp => {
                dispatch(authSuccess(resp.data.idToken, resp.data.localId));
                dispatch(checkAuthTimeOut(resp.data.expiresIn));
            })
            .catch(e => {
                console.log(e);
                dispatch(authFailed(e.response.data.error));
            });
    };
};
