import React, { Component } from 'react';
import classes from './Auth.module.css';

import { updateObject } from '../../shared/utility';

import Button  from '../../components/UI/Button/Button';
import Input from '../../components//UI/Input/Input';
import Spinner from '../../components/UI/Spinner/Spinner';

import { auth } from '../../store/actions/index';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';
import { setRedirectPath } from '../../store/actions/auth';

class Auth extends Component {
    state = {
        controls: {
            email: {
                elementType: 'input',
                elementConfig: {
                    type: 'email',
                    placeholder: 'Mail Address'
                },
                value: '',
                validation: {
                    required: true,
                    isEmail: true
                },
                valid: false,
                touched: false
            },
            password: {
                elementType: 'input',
                elementConfig: {
                    type: 'password',
                    placeholder: 'Password'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 6
                },
                valid: false,
                touched: false
            }
        },
        isSignup: true
    };

    componentDidMount() {
        if (!this.props.building && this.props.redirectPath !== '/') {
            this.props.onSetRedirectPath();
        }
    }



    inputChangeHandler = (event, controlName) => {
        const updatedControls = updateObject(this.state.controls, {
            [controlName]: updateObject(this.state.controls[controlName], {
                value: event.target.value,
                valid: this.checkValidation(event.target.value, this.state.controls[controlName].validation),
                touched: true
            })
        });

        this.setState({ controls: updatedControls });
    };

    submitHandler = (e) => {
        e.preventDefault();
        this.props.onAuth(this.state.controls.email.value, this.state.controls.password.value, this.state.isSignup);
    };

    switchAuthModeHandler = () => {
        this.setState(prevState => {
            return { isSignup: !prevState.isSignup }
        });
    };

    render() {
        const formElements = [];
        for (let key in this.state.controls) {
            formElements.push({
                id: key,
                config: this.state.controls[key]
            });
        }

        const form = this.props.loading ? <Spinner /> : formElements.map(formElement => (
            <Input 
                key={formElement.id}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                invalid={!formElement.config.valid}
                changed={(event) => this.inputChangeHandler(event, formElement.id)}
                touched={formElement.config.touched}
                shouldValidate={formElement.config.validation}
                autocomplete='on' />
        ));

        const errorMessage = this.props.error ? <p>{this.props.error.message}</p>: null;

        const authRedirect = this.props.isAuth ? <Redirect to={this.props.redirectPath} /> : null;

        return(
            <div className={classes.Auth}>
                {authRedirect}
                {errorMessage}
                <form onSubmit={this.submitHandler}>
                    {form}
                    <Button>SUBMIT</Button>
                </form>
                <Button
                    clicked={this.switchAuthModeHandler}
                    type='Danger'>SWITCH TO {this.state.isSignup ? 'SIGN UP' : 'SIGN IN'}</Button>
            </div>
        )
    }
}

const matStateToProps = state => {
    return {
        loading: state.auth.isLoading,
        error: state.auth.error,
        isAuth: state.auth.token !== null,
        building: state.burgerBuilder.building,
        redirectPath: state.auth.redirectPath
    };
};

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (email, password, isSignup) => dispatch(auth(email, password, isSignup)),
        onSetRedirectPath: () => dispatch(setRedirectPath('/'))
    };
};

export default connect(matStateToProps, mapDispatchToProps)(Auth);
