import React from 'react';
import classes from './Input.module.css';

const Input = ({elementType, elementConfig, value, label}) => {
    let inputElement = null;
    switch (elementType) {
        case ('input'):
            inputElement = <input
                                className={classes.InputElement}
                                value={value}
                                {...elementConfig} />;
            break;
        case ('textarea'):
            inputElement = <textarea
                                className={classes.InputElement}
                                {...elementConfig} />
            break;
        case ('select'):
            inputElement = <select className={classes.InputElement}>

                            </select>
            break;
        default:
            inputElement = <input 
                                className={classes.InputElement}
                                value={value}
                                {...elementConfig}  />
    }

    const labelElemtn = label ? <label className={classes.label}>{label}</label> : null;
    return (
        <div className={classes.Input}>
            {labelElemtn}
            {inputElement}
        </div>
    );
};

export default Input;
