import axios from 'axios';
import { AUTH_START, AUTH_FAILED, AUTH_SUCCESS, AUTH_LOGOUT, SET_AUTH_REDIRECT } from './actionTypes';

export const authStart = () => {
    return {
        type: AUTH_START
    }
};

export const authSuccess = (token, userId) => {
    return {
        type: AUTH_SUCCESS,
        token: token,
        userId: userId
    }
};

export const authFailed = (error) => {
    return {
        type: AUTH_FAILED,
        error: error
    };
};

export const logout = () => {
    localStorage.setItem('token', resp.data.idToken);
                localStorage.setItem('expirationDate', expirationDate);
    return {
        type: AUTH_LOGOUT
    };
};

export const checkAuthTimeOut = (expirationTime) => {
    return dispatch => {
        setTimeout(() => {
            dispatch(logout());
        }, expirationTime * 1000);
    };
};

export const auth = (email, password, isSignup = false) => {
    return dispatch => {
        dispatch(authStart());
        const authData = {
            email: email,
            password: password,
            returnSecureToken: true
        };

        let url = isSignup ?
            'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCXyYykiJQjUafDFB18wiFqDXo21AdZ6L8' :
            'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCXyYykiJQjUafDFB18wiFqDXo21AdZ6L8';

        axios.post(url, authData)
            .then(resp => {
                const expirationDate = new Date(new Date().getTime() + resp.data.expiresIn * 1000);
                localStorage.setItem('token', resp.data.idToken);
                localStorage.setItem('expirationDate', expirationDate);
                dispatch(authSuccess(resp.data.idToken, resp.data.localId));
                dispatch(checkAuthTimeOut(resp.data.expiresIn));
            })
            .catch(e => {
                console.log(e);
                dispatch(authFailed(e.response.data.error));
            });
    };
};

export const setRedirectPath = (path) => {
    return {
        type: SET_AUTH_REDIRECT,
        path: path
    };
};
