import {  AUTH_START, AUTH_FAILED, AUTH_SUCCESS, AUTH_LOGOUT } from '../actions/actionTypes';
import { updateObject } from '../utilities/utility';

const initialState = {
    token: null,
    userId: null,
    error: null,
    isLoading: false
};

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case AUTH_START:
            return updateObject(state, { error: null, isLoading: true });
        case AUTH_SUCCESS:
            return updateObject(state, {
                    token: action.token,
                    userId: action.userId,
                    error: null,
                    isLoading: false
                }
            );
        case AUTH_FAILED:
            return updateObject(state, { error: action.error, isLoading: false});
        case AUTH_LOGOUT:
            return updateObject(state, {})
        default:
            return state;
    }
};

export default reducer;
