import React from 'react';
import classes from './Input.module.css';

const Input = ({elementType, elementConfig, value, label, changed, invalid, shouldValidate = false}) => {
    let inputElement = [null];
    const inputCLasses = [classes.InputElement];

    if (invalid && shouldValidate && touched) {
        inputCLasses.push(classes.Invalid);
    }

    switch (elementType) {
        case ('input'):
            inputElement = <input
                                className={inputCLasses.join(' ')}
                                value={value}
                                {...elementConfig}
                                onChange={changed} />;
            break;
        case ('textarea'):
            inputElement = <textarea
                                className={inputCLasses.join(' ')}
                                {...elementConfig}
                                onChange={changed} />
            break;
        case ('select'):
            inputElement = <select
                                className={inputCLasses.join(' ')}
                                value={value}
                                onChange={changed}>
                                    {elementConfig.options.map(option => (
                                        <option 
                                            value={option.value}
                                            key={option.value}>
                                                {option.display}
                                        </option>
                                    ))}
                            </select>
            break;
        default:
            inputElement = <input 
                                className={inputCLasses.join(' ')}
                                value={value}
                                {...elementConfig}
                                onChange={changed}  />
    }

    const labelElemtn = label ? <label className={inputCLasses.join(' ')}>{label}</label> : null;
    return (
        <div className={classes.Input}>
            {labelElemtn}
            {inputElement}
        </div>
    );
};

export default Input;
