import React from 'react';
import classes from './Input.module.css';

const Input = ({inputType, label, type, name, value}) => {
    let inputElement = null;
    switch (type) {
        case ('input'):
            inputElement = <input className={classes.InputElement} type={type} name={name} value={value} />;
            break;
        case ('textarea'):
            inputElement = <textarea className={classes.InputElement} name={name} />
            break;
        default:
            inputElement = <input className={classes.InputElement} type={type} name={name} value={value} {...props} />
    }
    return (
        <div className={classes.Input}>
            <label className={classes.label}>{label}</label>
            {inputElement}
        </div>
    );
};

export default Input;
