import { PURCHASE_BURGER_SUCCESS, PURCHASE_BURGER_FAILED } from '../actions/actionTypes';
import axios from '../../../axios-orders';

export const purchaseBurgerSuccess = (id, orderData) => {
    return {
        type: PURCHASE_BURGER_SUCCESS,
        id: id,
        orderData: orderData
    };
};

export const purchaseBurgerFailed = (error) => {
    return {
        type: PURCHASE_BURGER_FAILED,
        error: error
    };
};

export const purchaseOrderStart = (orderData) => {
    return dispatch => {
        axios.post('/orders.json', orderData)
            .then(response => {
                dispatch(purchaseBurgerSuccess());
            }).catch(e => {
                console.log(e);
            });
    };
};
