import axios from 'axios';
import { AUTH_START, AUTH_FAILED, AUTH_SUCCESS } from './actionTypes';

export const authStart = () => {
    return {
        type: AUTH_START
    }
};

export const authSuccess = (authData) => {
    return {
        type: AUTH_SUCCESS,
        authData: authData
    }
};

export const authFailed = (error) => {
    return {
        type: AUTH_FAILED,
        error: error
    };
};

export const auth = (email, password) => {
    return dispatch => {
        dispatch(authStart());
        axios.post();
    };
};
