import axios from '../../axios-orders';
import { put } from 'redux-saga/effects';
import { purchaseBurgerStart, purchaseBurgerSuccess, purchaseBurgerFailed } from '../actions/index';

export function* purchaseBurgerSaga(action) {
    yield put(purchaseBurgerStart());
    try {
        const response = axios.post(`/orders.json?auth=${action.token}`, action.orderData);
        yield put(purchaseBurgerSuccess(response.data.name, action.orderData));
    } catch(e) {
        yield put(purchaseBurgerFailed());
    }
}
