import React, { Component, Fragment } from 'react';
import Burguer from '../../components/Burguer/Burguer';
import BuildControls from '../../components/Burguer/BuildControls/BuildControls';
import Modal from '../../components/UI/Modal/Modal';
import OrderSummary from '../../components/Burguer/OrderSummary/OrderSummary';
import Spinner from '../../components/UI/Spinner/Spinner';
import withErrorHandler from '../hoc/withErrorHandler/withErrorHandler';
import axios from '../../axios-orders';
import { connect } from 'react-redux';
import { addIngredient, removeIngredient, getIngredients } from '../../store/actions/index';

class BurguerBuilder extends Component {
    state = {
        pusrchasing: false,
    };

    componentDidMount() {
        this.props.getIngredients();
    }

    updatePurchaseState = (ingredients) => {
        const sum = Object.keys(ingredients)
            .map(key => ingredients[key])
            .reduce((sum, el) => {
                return sum + el;
            }, 0);
        return sum > 0;
    };

    purchaseHandler = () => {
        this.setState({pusrchasing: true});
    };

    purchaseCancelHandler = () => {
        this.setState({pusrchasing: false});
    };

    purchaseContinueHandler = () => {
        this.props.history.push('/checkout');
    };

    render() {
        const disableInfo = {
            ...this.props.ingredients
        };

        for (let key in disableInfo) {
            disableInfo[key] = disableInfo[key] <= 0;
        }

        let orderSummary = this.props.ingredients == null ?
                <Spinner /> :
                <OrderSummary
                    ingredients={this.props.ingredients}
                    purchaseCancel={this.purchaseCancelHandler}
                    purchaseContinue={this.purchaseContinueHandler}
                    price={this.props.totalPrice} />;

        let burguer = this.props.ingredients && this.props.error ? (
            <Fragment>
                <Burguer ingredients={this.props.ingredients} />
                <BuildControls
                    ingredientAdded={this.props.onIngredientAdded}
                    ingredientRemoved={this.props.onIngredientRemoved}
                    disabled={disableInfo}
                    purchasable={this.updatePurchaseState(this.props.ingredients)}
                    price={this.props.totalPrice}
                    ordered={this.purchaseHandler} />
            </Fragment> 
        ) : <p>Ingredients can't be loaded</p>;

        return (
            <Fragment>
                <Modal show={this.state.pusrchasing} modalClose={this.purchaseCancelHandler}>
                    {orderSummary}
                </Modal>
                {burguer}
            </Fragment>
        );
    }
}

const mapStateToProps = state => {
    return {
        ingredients: state.ingredients,
        totalPrice: state.totalPrice,
        error: state.error
    };
};

const mapDispatchToProps = dispatch => {
    return {
        onIngredientAdded: (name) => dispatch(addIngredient(name)),
        onIngredientRemoved: (name) => dispatch(removeIngredient(name)),
        getIngredients: () => dispatch(getIngredients())
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(withErrorHandler(BurguerBuilder, axios));
